import React from 'react';

import CalcButton from '../components/CalcButton';


// 計算機 App
class CalcApp extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      current:0
    };
  }

    resetState = () => this.setState(state => ({ current: 0}));

    handelNum = ()=> this.setState(state => ({ current: 7}));
    showNotImplemented() {
        console.log("靠")
    }

  render() {
    return (
      <div className="calc-app">
        <div className="calc-container">
          <div className="calc-output">
            <div className="calc-display">{this.state.current}</div>
          </div>
          <div className="calc-row">
            <CalcButton onClick={this.resetState}>AC</CalcButton>
            <CalcButton onClick={this.showNotImplemented}>+/-</CalcButton>
            <CalcButton onClick={this.showNotImplemented}>%</CalcButton>
            <CalcButton className="calc-operator">÷</CalcButton>
          </div>
          <div className="calc-row">
            <CalcButton className="calc-number" onClick={this.handelNum} >7</CalcButton>
            <CalcButton className="calc-number" onClick={this.handelNum}>8</CalcButton>
            <CalcButton className="calc-number" onClick={this.handelNum}>9</CalcButton>
            <CalcButton className="calc-operator">x</CalcButton>
          </div>
          <div className="calc-row">
            <CalcButton className="calc-number" onClick={this.handelNum}>4</CalcButton>
            <CalcButton onClick={this.handelNum}>5</CalcButton>
            <CalcButton onClick={this.handelNum}>6</CalcButton>
            <CalcButton className="calc-operator">-</CalcButton>
          </div>
          <div className="calc-row">
            <CalcButton className="calc-number">1</CalcButton>
            <CalcButton onClick={this.handelNum}>2</CalcButton>
            <CalcButton onClick={this.handelNum}>3</CalcButton>
            <CalcButton className="calc-operator">+</CalcButton>
          </div>
          <div className="calc-row">
            <CalcButton onClick={this.handelNum} className="bigger-btn">0</CalcButton>
            <CalcButton onClick={this.showNotImplemented}>.</CalcButton>
            <CalcButton className="calc-operator">=</CalcButton>
          </div>


        </div>
      </div>
    );
  }
}

export default CalcApp;
